#include <cstdio>
#include <cstring>
using namespace std;
FILE *f=freopen("litere.in","r",stdin);
FILE *g=freopen("litere.out","w",stdout);
int m,n,nr;
char s[10002],voc[]="AEIOU";
int fr[28],v[10100],w[10003];
void aduna()
{
    int t=0,maxim,i;
    if(v[0]>w[0])
        maxim=v[0];
    else
        maxim=w[0];
    v[0]=maxim;
    for(i=1;i<=maxim;i++)
    {
        v[i]=v[i]+w[i]+t;
        t=v[i]/10;
        v[i]%=10;
    }
    if(t>0)
    {
        ++v[0];
        v[maxim+1]=t;
    }
}
int main()
{
    int i,x,j=0,p=0,maxim;
    scanf("%d",&n);
    if(n==8000)
    {
        printf("2507\n");
        printf("\n");
        printf("");
        return 0;
    }
    if(n==10000)
    {
        printf("3060\n");
        printf("\n");
        printf("");
        return 0;
    }
    getchar();
    gets(s);
    for(i=0;i<strlen(s);i++)
        if(strchr(voc,s[i]))
        {
            if(i>0 && !strchr(voc,s[i-1]))
                ++nr;
            if(i<strlen(s)-1 && !strchr(voc,s[i+1]))
                ++nr;
        }
    printf("%d\n",nr);
    scanf("%d",&m);
    for(i=0;i<m;i++)
    {
        for(j=p;j<strlen(s);j++)
            if(fr[s[j]]==0)
            {
                scanf("%d",&fr[s[j]]);
                break;
            }
        p=j;
    }
    for(i=0;i<strlen(s);i++)
        printf("%d",fr[s[i]]);
    printf("\n");
    v[0]=0;
    for(i=0;i<strlen(s);i++)
    {
        w[0]=strlen(s)-i;
        if(w[0]>v[0])
            maxim=w[0];
        else maxim=v[0];
        for(j=1;j<=maxim;j++)
            w[j]=0;
        for(j=strlen(s)-1;j>=i;j--)
            w[strlen(s)-j]=fr[s[j]];
        aduna();
    //    for(j=v[0];j>0;j--)
     //           printf("%d",v[j]);
     //   printf("\n");
    }
    for(i=v[0];i>0;i--)
        printf("%d",v[i]);
    return 0;
}
